		CASE	OBJECT ;-----------------------------------------------;;   Imported addresses;;-----------------------------------------------	import _THErrNum,_THInfo,_THShutDown,_THStartUp,_THWord,_THXVal;-----------------------------------------------;;   Forward addresses and entries;;-----------------------------------------------	ENTRY	_THretaddr		PRINT	OFF *****************************************************************															  ;*   THINT.SRC - Assembly-language interface routines for TH	;*															  ;*   Robert A. Hearn											;*   StyleWare, Inc.					 9/12/87				;*															  ;****************************************************************	LOAD 'macros.dump'		****************************************************************THStartUp	PROC		EXPORT			;Using THIntData		phb					  ; must do this since we can't screw		pulllong >_THretaddr		; up the stack		phk		plb		call _THStartUp		moveword _THXVal,x		pushlong _THretaddr		plb		lda >_THErrNum		cmp #1		rtl		ENDP****************************************************************THShutDown	PROC		EXPORT			;Using THIntData		phb					  ; must do this since we can't screw		pulllong >_THretaddr		; up the stack		phk		plb		call _THShutDown		moveword _THXVal,x		pushlong _THretaddr		plb		lda >_THErrNum		cmp #1		rtl		ENDP****************************************************************THWord	PROC		EXPORT			;Using THIntData		phb					  ; must do this since we can't screw		pulllong >_THretaddr		; up the stack		phk		plb		call _THWord		moveword _THXVal,x		pushlong _THretaddr		plb		lda >_THErrNum		cmp #1		rtl		ENDP****************************************************************THInfo	PROC		EXPORT			;Using THIntData		phb					  ; must do this since we can't screw		pulllong >_THretaddr		; up the stack		phk		plb		call _THInfo		moveword _THXVal,x		pushlong _THretaddr		plb		lda >_THErrNum		cmp #1		rtl		ENDP*****************************************************************THIntData	PROC		EXPORT		EXPORT	_THretaddr_THretaddr		DS.B 4		ENDP		END